#pragma config(Motor,  motor1,          leftDrive,     tmotorVexIQ, PIDControl, encoder)
#pragma config(Motor,  motor4,          conveyor,      tmotorVexIQ, PIDControl, reversed, encoder)
#pragma config(Motor,  motor6,          rightDrive,    tmotorVexIQ, PIDControl, reversed, encoder)
#pragma config(Motor,  motor10,         dumper,        tmotorVexIQ, PIDControl, encoder)
#pragma config(Motor,  motor11,         revolver,      tmotorVexIQ, PIDControl, encoder)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

task main()
{
		while (true)
		{
				motor[leftDrive] = vexRT[ChA];
				motor[rightDrive] = vexRT[ChD];
				motor[leftbutton] = - (vexRT[ChB] + vexRT[ChC]) / 3;

				int forkLiftValue = 0;
				if (vexRT[BtnLUp] > 0 || vexRT[BtnRUp] > 0) {
						forkLiftValue = 100;
				} else if (vexRT[BtnLDown] > 0 || vexRT[BtnRDown] > 0) {
						forkLiftValue = -100;
				}

				motor[ft] = forkLiftValue;
				motor[rightForkLift] = forkLiftValue;

				//writeDebugStreamLine("%d", vexRT[ChC]);
		}
}
